import { StyleSheet } from 'react-native';

export const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    paddingTop: Platform.OS === 'android' ? 10 : 0, // Androidの場合、セーフエリアに対応するために25ポイント追加
  },
  container: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  currentTime: {
    fontSize: 24,
    marginBottom: 10,
    fontWeight: 'bold',
  },
    portTitle: {
    fontSize: 24,
    marginTop: 0,
    marginBottom: 15,
    fontWeight: 'bold',
  },
  kagoFrame:{
    marginTop: 10,
    backgroundColor: '#EBEBEB',
    borderWidth: 1,
    borderColor: '#5F3770',
    padding: 20,
    borderRadius: 5,
    alignItems: 'center',
  },
  sakuraFrame:{
    marginTop: 10,
    backgroundColor: '#FCDCE0',
    borderWidth: 1,
    borderColor: '#C87D99',
    padding: 20,
    borderRadius: 5,
    alignItems: 'center',
  },
  nextDeparture: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  headLineNews:{
    marginTop: 50,
    marginBottom: 0,
    borderWidth: 1,
    borderColor: '#1C1C1C',
    margin: 'auto',
    width: 300,
    height: 30,
    borderRadius: 255,
    alignItems: 'center',
  },
  bottomColumn: {
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  linkButtonText: {
    backgroundColor: '#EBEBEB',
    padding: 20,
    borderRadius: 5,
    fontSize: 16,
    fontWeight: 'bold',
  },  
  linkButtonTop: {
    marginTop: 80,
    padding: 10,
  },
  linkButton: {
    marginTop: 10,
    padding: 10,
  },
  linkButtonImage: {
    width: 200,
    height: 60,
  },
});
